import { useState } from "react";

export default function Todo() {
  const [todos, setTodos] = useState([]);
  const [inputValue, setinputValue] = useState("");

  function addTodos() {
    setTodos([...todos, inputValue]);
    setinputValue("");
  }

  function removehandler(index){
    const selectedTodos = todos.filter((_, loopIndex) => loopIndex !== index);
    setinputValue(selectedTodos);
    // setTodos(newTodos);
  }

  function editHandler(index){
    const selectedTodo = todos.filter((_ , loopIndex) => loopIndex === index);
    setinputValue(() => selectedTodo);
    let copyTodos = [...todos];
    copyTodos[index] = inputValue
    setTodos(copyTodos);
    setinputValue('');
  }

  return (
    <div>
      <h1>Todos</h1>
      <input
        type="text"
        name="input_todo"
        onChange={(e) => {
          setinputValue(e.target.value);
        }}
        value={inputValue}
      ></input>
      <button onClick={addTodos}>Add</button>

      <ul>
        {todos.map((todo, index) => (
            <div>
                <li key={index}>{todo}</li> 
                <button onClick={(e) => {e.stopPropagation(); removehandler(index)}}>X</button>
                <button onClick={(e) =>{e.stopPropagation(); editHandler(index)}}>Edit</button>
            </div>
        ))}
      </ul>
    </div>
  );
}
