import { useState } from "react";

export default function Todo() {
  const [todos, setTodos] = useState([]);
  const [inputValue, setinputValue] = useState("");
  const[isEditable , setIsEditable] = useState(true);
  const[newEditableInput , setNewEdiableInput] = useState('');

  function addTodos() {
    setTodos([...todos, inputValue]);
    setinputValue("");
  }

  function removehandler(index){
    const selectedTodos = todos.filter((_, loopIndex) => loopIndex !== index);
    setinputValue(selectedTodos);
    // setTodos(newTodos);
  }

  function editHandler(index){
    const selectedTodo = todos.filter((_ , loopIndex) => loopIndex === index);
    setinputValue(() => selectedTodo);
    let copyTodos = [...todos];
    copyTodos[index] = inputValue
    setTodos(copyTodos);
    setinputValue('');
  }

  return (
    <div>
      <h1>Todos</h1>
      <input
        type="text"
        name="input_todo"
        onChange={(e) => {
          setinputValue(e.target.value);
        }}
        value={inputValue}
      ></input>
      <button onClick={addTodos}>Add</button>

      <ul>
        {todos.map((todo, index) => (
            <div>
                 {isEditable ? <li key={index}>{todo}</li> : 
                 <input 
                    type="text" 
                    value={newEditableInput}
                    onChange={(e)=> setIsEditable(e.target.value)}
                 ></input> } 
                <button onClick={(e) => {e.stopPropagation(); removehandler(index)}}>X</button>
                <button onClick={(e) =>{e.stopPropagation(); editHandler(index) ; setIsEditable(() => !isEditable)}}>Edit</button>
            </div>
        ))}
      </ul>
    </div>
  );
}
